exports.command = function(cssSelector, callback){
  var self = this;
  if(typeof cssSelector === 'function'){
    return callback(new Error('Error: css selector value is not valid!'));
  }

  self.element("css selector", cssSelector, function(err, result){
    if(err && typeof callback == 'function') return callback(err);
    self.elementIdSelected(result.value.ELEMENT, function(err, result){
      if(err && typeof callback == 'function') return callback(err);    
      if(typeof callback == 'function' && result.value == false){
        return callback(new Error('Element ' + cssSelector + ' not selected' ));
      }
      if (typeof callback === 'function'){
        callback(null, result.value);
      }
    });
  });
};