var http = require("http");


exports.command = function(script, callback) 
{
	
	var commandOptions =  {
		path: "execute",
		method: "POST"
	}
	
	var request = this.createRequest(commandOptions, this.proxyResponse(callback));
	var data = JSON.stringify( {"script": script, args: []} );
	request.write(data);
	request.end();
};